        public static string GetSlavNum(int num)
        {
            string result;
            string temp = "·";
            List<int> numbers = StatFunc.SplitNum(num);

            // Тысячи
            if (numbers.Count > 3)
            {
                switch (numbers[3])
                {
                    case 1: temp += Letter.letter1[1]; break;
                    case 2: temp += Letter.letter3[1]; break;
                    case 3: temp += Letter.letter4[1]; break;
                    case 4: temp += Letter.letter5[1]; break;
                    case 5: temp += Letter.letter6[1]; break;
                    case 6: temp += Letter.letter9[1]; break;
                    case 7: temp += Letter.letter10[1]; break;
                    case 8: temp += Letter.letter11[1]; break;
                    case 9: temp += Letter.letter47[1]; break;
                }
            }

            // Сотни
            if (numbers.Count > 2)
            {
                switch (numbers[2])
                {
                    case 1: temp += Letter.letter21[1]; break;
                    case 2: temp += Letter.letter22[1]; break;
                    case 3: temp += Letter.letter23[1]; break;
                    case 4: temp += Letter.letter24[1]; break;
                    case 5: temp += Letter.letter26[1]; break;
                    case 6: temp += Letter.letter27[1]; break;
                    case 7: temp += Letter.letter46[1]; break;
                    case 8: temp += Letter.letter28[1]; break;
                    case 9: temp += Letter.letter29[1]; break;
                }
            }

            // Десятки
            if (numbers.Count > 1)
            {
                switch (numbers[1])
                {
                    case 1: temp += Letter.letter12[1]; break;
                    case 2: temp += Letter.letter15[1]; break;
                    case 3: temp += Letter.letter16[1]; break;
                    case 4: temp += Letter.letter17[1]; break;
                    case 5: temp += Letter.letter18[1]; break;
                    case 6: temp += Letter.letter45[1]; break;
                    case 7: temp += Letter.letter19[1]; break;
                    case 8: temp += Letter.letter20[1]; break;
                    case 9: temp += Letter.letter30[1]; break;
                }
            }

            // Единицы
            switch (numbers[0])
            {
                case 1: temp += Letter.letter1[1]; break;
                case 2: temp += Letter.letter3[1]; break;
                case 3: temp += Letter.letter4[1]; break;
                case 4: temp += Letter.letter5[1]; break;
                case 5: temp += Letter.letter6[1]; break;
                case 6: temp += Letter.letter9[1]; break;
                case 7: temp += Letter.letter10[1]; break;
                case 8: temp += Letter.letter11[1]; break;
                case 9: temp += Letter.letter47[1]; break;
            }

            if (num % 100 > 10 && num % 100 < 20)
            {
                char[] ch = temp.ToCharArray();
                char chTemp = ch[^2];
                ch[^2] = ch[^1];
                ch[^1] = chTemp;
                result = new string(ch);
            }
            else
            {
                result = temp;
            }

            return result + "·";
        }
